script:
frappe.ui.form.on('Travel Request', {
	refresh(frm) {
		frm.set_query('employee', function() {
            return {
                filters: {
                    user_id: frappe.session.user
                }
            };
        });
	}
})

frappe.ui.form.on('Work Continuity Plan', {
	refresh(frm) {
     // Insert HTML using jQuery
        frm.dashboard.add_section(`
        <h2>IntelliSOFT CONSULTING Work Continuity Plan</h2>
            <br>
            <h3>Leave Checklist</h3>
            <ol>
                <li>Set out-of-office Autoresponder.</li>
                <li>Set out-of-office calender.</li>
                <li>Log off email & work docs.</li>
            </ol>
        `);	
    }
});

frappe.ui.form.on('Travel Request', {
    refresh: function(frm) {
        // Insert HTML using jQuery
        frm.dashboard.add_section(`<br>
        <h2>IntelliSOFT CONSULTING TRAVEL AUTHORIZATION FORM <h2>
        <br>
        <h3>Instructions</h3>
        <ol>
            <li>Please fill out the Travel Authorization Form below.</li>
            <li>Obtain approval from your direct supervisor and HR/Operations and Finance Office prior to making reservations.</li>
            <li>Attach a detailed travel budget using the ICL travel budget template.</li>
        </ol>
        `);
    }
});
